using namespace std;

bool isok(const string& s1, const string& s2) {
    auto diff = 0;
    auto i1 = 0, i2 = 0;
    while (i1 < s1.size() and i2 < s2.size()) {
        if (s1[i1] != s2[i2]) i2++, diff++;
        else i1++, i2++;
    }
    if (i1 == i2) diff++;
    return diff == 1;
}

class Solution {
public:
    int longestStrChain(vector<string> words) {
        // O(N^2), 40ms
        auto n = words.size();

        auto cmp = [&](const string& s1, const string& s2) { 
            return s1.size() < s2.size();
        };
        sort(words.begin(), words.end(), cmp);

        vector<int> f(n, 1); int ans = 1;
        vector<int> pos = {0};
        for (size_t i = 0; i < n; i++)
        {
            if (words[i].size() != words[pos.back()].size()) pos.push_back(i);
        }
        pos.push_back(n);

        auto st = 0;
        for (size_t i = 0; i < n; i++)
        {
            if (words[i].size() == words[pos[st]].size()) st++;
            if (pos[st] == n) break;
            for (size_t j = pos[st]; j < pos[st+1]; j++)
            {
                if (isok(words[i], words[j])) f[j] = max(f[i]+1, f[j]), ans = max(ans, f[j]);
            }
        }

        return  ans;
    }
};


