#include "../utils/LeetCpp.utils.hpp"

class Solution {
public:
    int findSubstringInWraproundString(string p) {
        // O(N)
        int len = 0, prev = 0;
        int f[26]{}; 
        for (auto&& pi: p) {
            if ( pi - prev == 1 or (pi == 'a' and prev == 'z') ) len++;
            else len = 1;
            prev = pi;
            f[pi-'a'] = max(f[pi-'a'], len);
        }
        
        auto cnt = 0;
        for (auto&& fi: f) cnt += fi;
        return cnt;
    }
};


int main(int argc, char const *argv[])
{
    Solution sol;   
    cout << sol.findSubstringInWraproundString("") << endl;
    cout << sol.findSubstringInWraproundString("zab") << endl;
    cout << sol.findSubstringInWraproundString("a") << endl;
    cout << sol.findSubstringInWraproundString("cac") << endl;
    return 0;
}
